/*!********************************************************
************************ Hoverizr *************************

Hoverizr is an image manipulation jQuery plug in taking
advantage of the new canvas element which is getting more
and more popular.

Currently it allows you to use three different, dynamic
effects:
  1. Grayscale - Default
	2. Blur
	3. Color Inversion

You can define your own options or following the trends
it can be responsive by default.

You can read more about Hoverizr and see some Demos at:
		http://www.iliasiovis.com/hoverizr/

Fork on GitHub:
		https://github.com/iliasiovis/Hoverizr/

CC 2011 - Ilias Iovis | iliasiovis.com
***********************************************************
**********************************************************/
(function($){$.fn.hoverizr=function(options){var defaults={effect:"grayscale",overlay:"top",container:"overlayed",width:"responsive",height:"auto",stretch:"no",speedIn:"slow",speedOut:"fast",hover:true,};var options=$.extend(defaults,options);if($("img").css("maxWidth")!="none"){var maxwidth=1;$("img").css("maxWidth","none");}else{var maxwidth=0;}this.each(function(){$(this).wrap('<div class="'+options.container+'" />');$(this).parent("."+options.container+"").css({position:"relative"});$(this).parent("."+options.container+"").append('<canvas class="canv"></canvas>');$(this).next(".canv").css({position:"absolute",top:"0",left:"0","z-index":10});if(options.overlay=="top"){$(this).css({"z-index":-1});}else{$(this).css({"z-index":1});$(this).next(".canv").css({display:"none"});}var width=$(this).width();var height=$(this).height();$(this).next(".canv").attr({width:width,height:height});var canvas=$(this).next(".canv").get(0);var context=canvas.getContext("2d");var image=$(this).get(0);context.drawImage(image,0,0);if(options.effect!="noise"&&"blur"){try{try{var imgd=context.getImageData(0,0,width,height);}catch(e){netscape.security.PrivilegeManager.enablePrivilege("UniversalBrowserRead");var imgd=context.getImageData(0,0,width,height);}}catch(e){throw new Error("unable to access image data: "+e);}var pix=imgd.data;}switch(options.effect){case"invert":for(var i=0,n=pix.length;i<n;i+=4){pix[i]=255-pix[i];pix[i+1]=255-pix[i+1];pix[i+2]=255-pix[i+2];}break;case"blur":var pass,x,y;var imgb=new Image;imgb.src=$(this).attr("src");context.globalAlpha=0.0625;for(y=-3;y<3;y+=1){for(x=-3;x<3;x+=1){context.drawImage(imgb,x,y);}}context.globalAlpha=1;break;default:for(var i=0,n=pix.length;i<n;i+=4){var grayscale=pix[i]*0.3+pix[i+1]*0.59+pix[i+2]*0.11;pix[i]=grayscale;pix[i+1]=grayscale;pix[i+2]=grayscale;}break;}if(options.effect!=("blur"||"noise")){context.putImageData(imgd,0,0);}if(options.width=="responsive"){$(this).next(".canv").css({"max-width":"100%"});$(this).css({"max-width":"100%"});$(this).parent("."+options.container+"").css({width:"100%"});}else{if(options.stretch=="no"){$(this).parent("."+options.container+"").css({width:options.width,height:options.height,overflow:"hidden"});}else{$(this).next(".canv").css({width:options.width,height:options.height});$(this).css({width:options.width,height:options.height});$(this).parent("."+options.container+"").css({width:options.width,height:options.height});}}});if(maxwidth==1){$("img").css("maxWidth","100%");}if(options.hover===true){if(options.overlay=="top"){this.parent("."+options.container+"").hover(function(){$(this).children(".canv").stop(true,true).fadeOut(options.speedOut);},function(){$(this).children(".canv").stop(true,true).fadeIn(options.speedIn);});}else{this.parent("."+options.container+"").hover(function(){$(this).children(".canv").stop(true,true).fadeIn(options.speedOut);},function(){$(this).children(".canv").stop(true,true).fadeOut(options.speedIn);});}}};})(jQuery);